import React from "react";
import styled from "styled-components";


const WeatherDiv = styled.div `
    display: flex;
    flex-direction: column;
    width: 400px;
    align-items: center;
    justify-content: center;
    margin: auto;
    background-color: #fefc81;
    opacity: .8;
    padding: 5px;
    border: 2px solid gold;
    border-radius: 20px;

    &:hover {
        opacity: 1;
        box-shadow: 5px 5px 10px black;
    }

`;

const WeatherInfo = styled.div `
    display: flex;
    justify-content: space-evenly;
    padding: 5px;
    width: 100%;
`;

const Span = styled.span `
    font-size: 1.4rem;
    font-weight: bolder;
    text-align: center;

`;
const Paragraph = styled.p `
    font-size: 1.2rem;

`;


function Api(props) {
  return (
    <>
    <WeatherDiv>
      <h1>Lancaster, PA</h1>
      <iframe src="http://free.timeanddate.com/clock/i6uvq75w/n4561/fs18/tt0/tw0" frameborder="0" width="334" height="23" color = "#fefc81"></iframe>
      <Paragraph>{props.weather.WeatherText}</Paragraph>
      <WeatherInfo>
      <Paragraph>Temperature: <Span>{props.temperature.Value}°</Span></Paragraph>
      <Paragraph>Real Feel: <Span>{props.realFeel.Value}°</Span></Paragraph>
      <Paragraph>Humidity: <Span>{props.weather.RelativeHumidity}</Span></Paragraph>
      </WeatherInfo>
    </WeatherDiv>
    </>
  );
}

export default Api;
