import React, {state, handleClick} from "react";
import styled from "styled-components";
import { Accordion, Icon } from 'semantic-ui-react'

const Frame = styled.iframe`
    margin: 20px auto;

`;

function Football () {
    state = { activeIndex: 0 }

  handleClick = (e, titleProps) => {
    const { index } = titleProps
    const { activeIndex } = this.state
    const newIndex = activeIndex === index ? -1 : index

    this.setState({ activeIndex: newIndex })
  };

  function render() {
    const { activeIndex } = this.state

    return (
        <>
        <Accordion>
        <Accordion.Title active={activeIndex === 0} index={0} onClick={this.handleClick}>
          <Icon name='dropdown' />
          Eagles Stats
        </Accordion.Title>
        <Accordion.Content active={activeIndex === 0}>
        <Frame id="n_1" src="https://www.numberfire.com/external/widgets/teams/philadelphia-eagles" width="480" height="400"></Frame>
        </Accordion.Content>

        <Accordion.Title active={activeIndex === 1} index={1} onClick={this.handleClick}>
          <Icon name='dropdown' />
          Top Player Rankings
        </Accordion.Title>
        <Accordion.Content active={activeIndex === 1}>
        <Frame id="n_0" src="https://www.numberfire.com/external/widgets/top-players" width="480" height="400"></Frame>
        </Accordion.Content>

        <Accordion.Title active={activeIndex === 2} index={2} onClick={this.handleClick}>
          <Icon name='dropdown' />
          How do you acquire a dog?
        </Accordion.Title>
        <Accordion.Content active={activeIndex === 2}>
          <p>
            Three common ways for a prospective owner to acquire a dog is from pet shops, private
            owners, or shelters.
          </p>
          <p>
            A pet shop may be the most convenient way to buy a dog. Buying a dog from a private
            owner allows you to assess the pedigree and upbringing of your dog before choosing to
            take it home. Lastly, finding your dog from a shelter, helps give a good home to a dog
            who may not find one so readily.
          </p>
        </Accordion.Content>
      </Accordion>

        </>
    )
 };
};

export default Football;
